memberdef.initializer:
= $derived.by(() => {
        if (data.length === 0) return 0;

        const maxVal = d3.max(data, d => {
            return keys.reduce((acc, k) => acc + (d[k] || 0), 0);
        });
        return maxVal || 10;
    })

= $derived.by(() => {
        const domain = xScale.domain();
        if (resolvedWidth < 500 && domain.length > 0) {
            const step = Math.ceil(30 / (resolvedWidth / domain.length)); 
            if (step > 1) {
                return domain.filter((_, i) => i % step === 0);
            }
        }
        return domain;
    })
